import React from "react";

require("./style.scss");

const SliderItem = ({ movie, width }) => {
	return (
		<div className="slider-item" style={{ width: `${width}%` }}>
			<img
				className="slider-image"
				src={`http://image.tmdb.org/t/p/w780${movie.backdrop_path}`}
				alt={movie.title}
			/>
			<div class="item item-1">
				<div class="body-item">
					<div class="body-item-1">
						<div class="play">
							<i class="icon-play"></i>
						</div>
					</div>
					<div class="title body-item-2">Breaking Bad</div>
					<div class="properties body-item-3">
						<span class="match">% 98 Match</span>
						<span class="year">2012</span>
						<span class="age-limit">13+</span>
						<span class="time">2h 13m</span>
					</div>
					<p class="description body-item-4">
						Lorem ipsum dolor sit amet consectetur adipisicing elit. Eius alias
						libero..
					</p>
					<div class="body-item-5">
						<i class="details-icon icon-chevron-down"></i>
					</div>
					<div class="icon-set body-item-6">
						<i class="icon-thumbs-up"></i>
						<i class="icon-thumbs-down"></i>
						<i class="icon-plus"></i>
					</div>
				</div>
			</div>
		</div>
		// <div class="item item-1">
		// 	<div class="body-item">
		// 		<div class="body-item-1">
		// 			<div class="play">
		// 				<i class="icon-play"></i>
		// 			</div>
		// 		</div>
		// 		<div class="title body-item-2">Breaking Bad</div>
		// 		<div class="properties body-item-3">
		// 			<span class="match">% 98 Match</span>
		// 			<span class="year">2012</span>
		// 			<span class="age-limit">13+</span>
		// 			<span class="time">2h 13m</span>
		// 		</div>
		// 		<p class="description body-item-4">
		// 			Lorem ipsum dolor sit amet consectetur adipisicing elit. Eius alias
		// 			libero..
		// 		</p>
		// 		<div class="body-item-5">
		// 			<i class="details-icon icon-chevron-down"></i>
		// 		</div>
		// 		<div class="icon-set body-item-6">
		// 			<i class="icon-thumbs-up"></i>
		// 			<i class="icon-thumbs-down"></i>
		// 			<i class="icon-plus"></i>
		// 		</div>
		// 	</div>
		// </div>
	);
};

export default SliderItem;
